import axios from "axios";
import { bus } from "@/bus";
export default {
  name: "my-center",
  data: function data() {
    return {
      Id: 1,
      pId: 1,
      data2: [],
      note: [],
      fileid: 1,
      curid: 1,
      tableData: [],
      name: "",
      menuname: "",
      totalcount: 1,
      curpage: 1,
      menuId: 0
    };
  },
  components: {},
  watch: {
    $route: function $route() {
      //浏览器默认改变路径后面带的参数不改变路径不会刷新整个页面，所以这里可以监听到参数变化，此时可以选择触发一些方法，也可以选择强制刷新整个页面，
      //这里选择强制刷新页面是因为el-menu 的default-active只有在渲染的时候才会变
      this.$router.go(0);
    }
  },
  methods: {
    getid: function getid(val) {
      var _this = this;

      axios.get("http://cybwmy.top:8082/menu/blogs/" + val).then(function (res) {
        _this.tableData = res.data.object.object;
        _this.totalcount = res.data.object.totalCount;

        _this.$nextTick(function () {
          bus.$emit("table", this.tableData);
          bus.$emit("total", this.totalcount);
        });
      });
      this.menuId = val;
      this.$nextTick(function () {
        bus.$emit("menuId", this.menuId);
      });
    },
    handleCurrentChange: function handleCurrentChange(val) {
      var _this2 = this;

      this.curpage = val;
      console.log(this.curpage);
      axios("http://cybwmy.top:8082.com/menu/blogs/" + this.menuId + "?currentPage=" + this.curpage).then(function (res) {
        console.log(res);
        _this2.tableData = res.data.object.object;
      });
    },
    getChileMenu: function getChileMenu(id) {
      var _this3 = this;

      axios("http://cybwmy.top:8082/menu/cMenu/" + id).then(function (res) {
        _this3.name = res.data.object.menuName;
        _this3.data2 = res.data.object.childen;
      });
    },
    getMenu: function getMenu() {
      var f = 0;

      if (localStorage.getItem("menuList") != null) {
        var data = JSON.parse(localStorage.getItem("menuList"));

        for (var i = 0; i < data.length; i++) {
          if (data[i].id == this.$route.query.pId) {
            f = 1;
            this.name = data[i].menuName;
            this.data2 = data[i].childen;
          }
        }
      }

      if (f == 0) {
        this.getChileMenu(this.$route.query.pId);
      }

      this.Id = this.$route.query.Id;
      this.pId = this.$route.query.pId;
    }
  },
  mounted: function mounted() {
    this.getMenu();
    this.getid(this.$route.query.Id);
  }
};
